WITH date_subq AS (SELECT d_date_sk FROM date_dim WHERE d_dow = 1 AND d_year IN (1998, 1999, 2000)), store_subq AS (SELECT s_store_sk, s_city FROM store WHERE s_number_employees BETWEEN 200 AND 295), hd_subq AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count = 5 OR hd_vehicle_count > 4), fact_join AS (SELECT ss.ss_ticket_number, ss.ss_customer_sk, ss.ss_addr_sk, st.s_city, ss.ss_coupon_amt, ss.ss_net_profit FROM store_sales ss INNER JOIN date_subq dt ON ss.ss_sold_date_sk = dt.d_date_sk INNER JOIN store_subq st ON ss.ss_store_sk = st.s_store_sk INNER JOIN hd_subq hd ON ss.ss_hdemo_sk = hd.hd_demo_sk), aggregate AS (SELECT ss_ticket_number, ss_customer_sk, s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM fact_join GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city) SELECT c_last_name, c_first_name, SUBSTRING(s_city, 1, 30), ss_ticket_number, amt, profit FROM aggregate ms INNER JOIN customer ON ss_customer_sk = c_customer_sk ORDER BY c_last_name, c_first_name, SUBSTRING(s_city, 1, 30), profit LIMIT 100