WITH ctr_all AS (SELECT cr_returning_customer_sk AS ctr_customer_sk, ca_state AS ctr_state, SUM(cr_return_amt_inc_tax) AS ctr_total_return FROM catalog_returns, date_dim, customer_address WHERE cr_returned_date_sk = d_date_sk AND d_year = 2002 AND cr_returning_addr_sk = ca_address_sk GROUP BY cr_returning_customer_sk, ca_state), ctr_state_avg AS (SELECT ctr_state, AVG(ctr_total_return) AS state_avg FROM ctr_all GROUP BY ctr_state), ctr_ca AS (SELECT ctr_all.ctr_customer_sk, ctr_all.ctr_state, ctr_all.ctr_total_return, c_customer_id, c_salutation, c_first_name, c_last_name, ca_street_number, ca_street_name, ca_street_type, ca_suite_number, ca_city, ca_county, ca_state, ca_zip, ca_country, ca_gmt_offset, ca_location_type FROM ctr_all, customer, customer_address WHERE ctr_all.ctr_customer_sk = c_customer_sk AND c_current_addr_sk = ca_address_sk AND ca_state = 'CA') SELECT c_customer_id, c_salutation, c_first_name, c_last_name, ca_street_number, ca_street_name, ca_street_type, ca_suite_number, ca_city, ca_county, ca_state, ca_zip, ca_country, ca_gmt_offset, ca_location_type, ctr_total_return FROM ctr_ca JOIN ctr_state_avg ON ctr_ca.ctr_state = ctr_state_avg.ctr_state WHERE ctr_ca.ctr_total_return > ctr_state_avg.state_avg * 1.2 ORDER BY c_customer_id, c_salutation, c_first_name, c_last_name, ca_street_number, ca_street_name, ca_street_type, ca_suite_number, ca_city, ca_county, ca_state, ca_zip, ca_country, ca_gmt_offset, ca_location_type, ctr_total_return LIMIT 100