WITH grouped_item AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 17 AND 47 AND i_manufact_id IN (639, 169, 138, 339) GROUP BY i_item_sk, i_item_id, i_item_desc, i_current_price), filtered_inventory_date AS (SELECT inv_item_sk FROM inventory JOIN date_dim ON inv_date_sk = d_date_sk WHERE d_date BETWEEN CAST('1999-07-09' AS DATE) AND (CAST('1999-07-09' AS DATE) + INTERVAL 60 DAY) AND inv_quantity_on_hand BETWEEN 100 AND 500 GROUP BY inv_item_sk), filtered_store_sales AS (SELECT ss_item_sk FROM store_sales GROUP BY ss_item_sk) SELECT gi.i_item_id, gi.i_item_desc, gi.i_current_price FROM grouped_item gi JOIN filtered_inventory_date fid ON gi.i_item_sk = fid.inv_item_sk JOIN filtered_store_sales fss ON gi.i_item_sk = fss.ss_item_sk ORDER BY gi.i_item_id LIMIT 100