WITH dim_item AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 17 AND 47 AND i_manufact_id IN (639, 169, 138, 339)), dim_inventory_date AS (SELECT di.i_item_sk, di.i_item_id, di.i_item_desc, di.i_current_price FROM dim_item di JOIN inventory inv ON inv.inv_item_sk = di.i_item_sk JOIN date_dim dd ON inv.inv_date_sk = dd.d_date_sk WHERE dd.d_date BETWEEN CAST('1999-07-09' AS DATE) AND (CAST('1999-07-09' AS DATE) + INTERVAL 60 DAY) AND inv.inv_quantity_on_hand BETWEEN 100 AND 500), key_list AS (SELECT i_item_sk FROM dim_inventory_date), bloom_join_store_sales AS (SELECT ss_item_sk FROM store_sales WHERE ss_item_sk IN (SELECT i_item_sk FROM key_list)) SELECT did.i_item_id, did.i_item_desc, did.i_current_price FROM dim_inventory_date did JOIN bloom_join_store_sales bj ON did.i_item_sk = bj.ss_item_sk GROUP BY did.i_item_id, did.i_item_desc, did.i_current_price ORDER BY did.i_item_id LIMIT 100