WITH filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Oakwood'), filtered_ib AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 5806 AND ib_upper_bound <= 55806), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics hd INNER JOIN filtered_ib ib ON hd.hd_income_band_sk = ib.ib_income_band_sk), filtered_c AS (SELECT c_customer_id, c_first_name, c_last_name, c_current_cdemo_sk FROM customer c INNER JOIN filtered_ca ca ON c.c_current_addr_sk = ca.ca_address_sk INNER JOIN filtered_hd hd ON c.c_current_hdemo_sk = hd.hd_demo_sk), filtered_cd AS (SELECT cd.cd_demo_sk, c.c_customer_id, c.c_first_name, c.c_last_name FROM customer_demographics cd INNER JOIN filtered_c c ON cd.cd_demo_sk = c.c_current_cdemo_sk), sr_join AS (SELECT cd.c_customer_id, COALESCE(cd.c_last_name, '') || ', ' || COALESCE(cd.c_first_name, '') AS customername FROM filtered_cd cd INNER JOIN store_returns sr ON sr.sr_cdemo_sk = cd.cd_demo_sk) SELECT c_customer_id AS customer_id, customername FROM sr_join ORDER BY c_customer_id ASC LIMIT 100