WITH ca_ib_filtered AS (SELECT ca_address_sk, ib_income_band_sk FROM customer_address CROSS JOIN income_band WHERE ca_city = 'Oakwood' AND ib_lower_bound >= 5806 AND ib_upper_bound <= 55806), hd_join AS (SELECT hd_demo_sk, ca_ib_filtered.ca_address_sk FROM household_demographics JOIN ca_ib_filtered ON hd_income_band_sk = ca_ib_filtered.ib_income_band_sk), c_join AS (SELECT c.c_customer_id, c.c_first_name, c.c_last_name, c.c_current_cdemo_sk FROM customer c JOIN ca_ib_filtered ON c.c_current_addr_sk = ca_ib_filtered.ca_address_sk JOIN hd_join ON c.c_current_hdemo_sk = hd_join.hd_demo_sk), cd_join AS (SELECT cd.cd_demo_sk, c_join.c_customer_id, c_join.c_first_name, c_join.c_last_name FROM customer_demographics cd JOIN c_join ON cd.cd_demo_sk = c_join.c_current_cdemo_sk), sr_join AS (SELECT cd_join.c_customer_id, COALESCE(cd_join.c_last_name,'') || ', ' || COALESCE(cd_join.c_first_name,'') AS customername FROM cd_join JOIN store_returns ON store_returns.sr_cdemo_sk = cd_join.cd_demo_sk), top_n AS (SELECT c_customer_id AS customer_id, customername FROM sr_join ORDER BY c_customer_id ASC LIMIT 100) SELECT customer_id, customername FROM top_n