WITH ca_cte AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Oakwood'), ib_cte AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 5806 AND ib_upper_bound <= 55806), hd_cte AS (SELECT hd_demo_sk, hd_income_band_sk FROM household_demographics), c_cte AS (SELECT c_customer_id, c_first_name, c_last_name, c_current_addr_sk, c_current_hdemo_sk, c_current_cdemo_sk FROM customer), cd_cte AS (SELECT cd_demo_sk FROM customer_demographics), combined AS (SELECT c.c_customer_id, c.c_first_name, c.c_last_name, cd.cd_demo_sk FROM ca_cte ca, ib_cte ib, hd_cte hd, c_cte c, cd_cte cd WHERE c.c_current_addr_sk = ca.ca_address_sk AND hd.hd_income_band_sk = ib.ib_income_band_sk AND c.c_current_hdemo_sk = hd.hd_demo_sk AND cd.cd_demo_sk = c.c_current_cdemo_sk), sr_join AS (SELECT c.c_customer_id, COALESCE(c.c_last_name,'') || ', ' || COALESCE(c.c_first_name,'') AS customername FROM combined c INNER JOIN store_returns sr ON sr.sr_cdemo_sk = c.cd_demo_sk) SELECT c_customer_id AS customer_id, customername FROM sr_join ORDER BY c_customer_id ASC LIMIT 100