WITH exact_keys AS (SELECT c.c_customer_id, c.c_first_name, c.c_last_name, cd.cd_demo_sk FROM customer c INNER JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk INNER JOIN customer_demographics cd ON cd.cd_demo_sk = c.c_current_cdemo_sk INNER JOIN household_demographics hd ON hd.hd_demo_sk = c.c_current_hdemo_sk INNER JOIN income_band ib ON hd.hd_income_band_sk = ib.ib_income_band_sk WHERE ca.ca_city = 'Oakwood' AND ib.ib_lower_bound >= 5806 AND ib.ib_upper_bound <= 5806 + 50000), sr_filter AS (SELECT c_customer_id, c_first_name, c_last_name FROM exact_keys ek WHERE EXISTS (SELECT 1 FROM store_returns sr WHERE sr.sr_cdemo_sk = ek.cd_demo_sk)), enrich AS (SELECT c_customer_id, COALESCE(c_last_name,'') || ', ' || COALESCE(c_first_name,'') AS customername FROM sr_filter), top_n AS (SELECT c_customer_id AS customer_id, customername FROM enrich ORDER BY c_customer_id ASC LIMIT 100) SELECT customer_id, customername FROM top_n