SELECT CAST(amc AS DECIMAL(15,4)) / CAST(pmc AS DECIMAL(15,4)) AS am_pm_ratio FROM (SELECT COUNT(*) FILTER (WHERE time_dim.t_hour BETWEEN 10 AND 11) AS amc, COUNT(*) FILTER (WHERE time_dim.t_hour BETWEEN 16 AND 17) AS pmc FROM web_sales INNER JOIN time_dim ON ws_sold_time_sk = t_time_sk INNER JOIN household_demographics ON ws_ship_hdemo_sk = hd_demo_sk INNER JOIN web_page ON ws_web_page_sk = wp_web_page_sk WHERE household_demographics.hd_dep_count = 2 AND web_page.wp_char_count BETWEEN 5000 AND 5200 AND (time_dim.t_hour BETWEEN 10 AND 11 OR time_dim.t_hour BETWEEN 16 AND 17)) ORDER BY am_pm_ratio LIMIT 100