WITH filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_manufact_id = 320), filtered_date AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL 90 DAY)), item_avg_discount AS (SELECT ws.ws_item_sk AS i_item_sk, AVG(ws.ws_ext_discount_amt) AS avg_disc FROM web_sales ws INNER JOIN filtered_date fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_item fi ON ws.ws_item_sk = fi.i_item_sk GROUP BY ws.ws_item_sk), main_join AS (SELECT ws.ws_ext_discount_amt FROM web_sales ws INNER JOIN filtered_item fi ON ws.ws_item_sk = fi.i_item_sk INNER JOIN filtered_date fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN item_avg_discount iad ON ws.ws_item_sk = iad.i_item_sk WHERE ws.ws_ext_discount_amt > 1.3 * iad.avg_disc), filter_agg AS (SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM main_join), top_n AS (SELECT "Excess Discount Amount" FROM filter_agg ORDER BY "Excess Discount Amount" ASC LIMIT 100) SELECT "Excess Discount Amount" FROM top_n