WITH filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_manufact_id = 320), filtered_date AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL 90 DAY)), web_sales_filtered AS (SELECT ws.ws_ext_discount_amt, ws.ws_item_sk FROM web_sales ws INNER JOIN filtered_date fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_item fi ON ws.ws_item_sk = fi.i_item_sk), enriched AS (SELECT ws_ext_discount_amt, AVG(ws_ext_discount_amt) OVER (PARTITION BY ws_item_sk) AS avg_disc FROM web_sales_filtered), agg AS (SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM enriched WHERE ws_ext_discount_amt > 1.3 * avg_disc), top_n AS (SELECT "Excess Discount Amount" FROM agg ORDER BY "Excess Discount Amount" ASC LIMIT 100) SELECT "Excess Discount Amount" FROM top_n