WITH filtered_returns_cte AS MATERIALIZED (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, sr_reason_sk AS ctr_reason_sk, SUM(SR_RETURN_AMT_INC_TAX) AS ctr_total_return FROM store_returns JOIN date_dim ON sr_returned_date_sk = d_date_sk WHERE d_year = 2002 AND sr_return_amt / sr_return_quantity BETWEEN 108 AND 167 GROUP BY sr_customer_sk, sr_store_sk, sr_reason_sk), store_avg_threshold_cte AS MATERIALIZED (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS store_avg_threshold FROM filtered_returns_cte GROUP BY ctr_store_sk) SELECT c_customer_id FROM filtered_returns_cte AS ctr1 JOIN store_avg_threshold_cte AS th ON ctr1.ctr_store_sk = th.ctr_store_sk JOIN store ON s_store_sk = ctr1.ctr_store_sk JOIN customer ON c_customer_sk = ctr1.ctr_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk WHERE ctr1.ctr_total_return > th.store_avg_threshold AND ctr1.ctr_reason_sk BETWEEN 43 AND 46 AND s_state IN ('IL', 'KY', 'TX') AND cd_marital_status IN ('M', 'M') AND cd_education_status IN ('Advanced Degree', 'College') AND cd_gender = 'F' AND c_birth_month = 2 AND c_birth_year BETWEEN 1965 AND 1971 ORDER BY c_customer_id LIMIT 100