WITH returns_with_store_avg_cte AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, sr_reason_sk AS ctr_reason_sk, SUM(sr_return_amt_inc_tax) AS ctr_total_return, AVG(SUM(sr_return_amt_inc_tax)) OVER (PARTITION BY sr_store_sk) * 1.2 AS store_avg_threshold FROM store_returns INNER JOIN date_dim ON sr_returned_date_sk = d_date_sk WHERE d_year = 2002 AND CASE WHEN sr_return_quantity > 0 THEN sr_return_amt / sr_return_quantity END BETWEEN 108 AND 167 GROUP BY sr_customer_sk, sr_store_sk, sr_reason_sk) SELECT c_customer_id FROM returns_with_store_avg_cte ctr1 INNER JOIN store ON s_store_sk = ctr1.ctr_store_sk INNER JOIN customer ON c_customer_sk = ctr1.ctr_customer_sk INNER JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk WHERE ctr1.ctr_total_return > ctr1.store_avg_threshold AND ctr1.ctr_reason_sk BETWEEN 43 AND 46 AND s_state IN ('IL', 'KY', 'TX') AND cd_marital_status IN ('M', 'M') AND cd_education_status IN ('Advanced Degree', 'College') AND cd_gender = 'F' AND c_birth_month = 2 AND c_birth_year BETWEEN 1965 AND 1971 ORDER BY c_customer_id LIMIT 100