WITH filtered_date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), prefactored_returns_cte AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, sr_reason_sk AS ctr_reason_sk, SUM(SR_RETURN_AMT_INC_TAX) AS ctr_total_return FROM store_returns INNER JOIN filtered_date_cte ON store_returns.sr_returned_date_sk = filtered_date_cte.d_date_sk WHERE sr_return_amt / sr_return_quantity BETWEEN 108 AND 167 GROUP BY sr_customer_sk, sr_store_sk, sr_reason_sk), store_avg_threshold_cte AS (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS store_avg_threshold FROM prefactored_returns_cte GROUP BY ctr_store_sk) SELECT c_customer_id FROM prefactored_returns_cte AS ctr1 INNER JOIN store_avg_threshold_cte AS th ON ctr1.ctr_store_sk = th.ctr_store_sk INNER JOIN store ON ctr1.ctr_store_sk = store.s_store_sk INNER JOIN customer ON ctr1.ctr_customer_sk = customer.c_customer_sk INNER JOIN customer_demographics ON customer.c_current_cdemo_sk = customer_demographics.cd_demo_sk WHERE ctr1.ctr_total_return > th.store_avg_threshold AND ctr1.ctr_reason_sk BETWEEN 43 AND 46 AND store.s_state IN ('IL', 'KY', 'TX') AND customer_demographics.cd_marital_status IN ('M', 'M') AND customer_demographics.cd_education_status IN ('Advanced Degree', 'College') AND customer_demographics.cd_gender = 'F' AND customer.c_birth_month = 2 AND customer.c_birth_year BETWEEN 1965 AND 1971 ORDER BY c_customer_id LIMIT 100