WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy BETWEEN 5 AND 8), filtered_items AS (SELECT i_item_sk FROM item WHERE i_category IN ('Children', 'Home', 'Women') AND i_manager_id BETWEEN 91 AND 100), sales_consolidated AS (SELECT ss_customer_sk AS customer_sk FROM store_sales ss WHERE EXISTS (SELECT 1 FROM filtered_dates fd WHERE ss.ss_sold_date_sk = fd.d_date_sk) AND EXISTS (SELECT 1 FROM filtered_items fi WHERE ss.ss_item_sk = fi.i_item_sk) AND ss.ss_sales_price / ss.ss_list_price BETWEEN 0.65 AND 0.75 UNION ALL SELECT ws_bill_customer_sk AS customer_sk FROM web_sales ws WHERE EXISTS (SELECT 1 FROM filtered_dates fd WHERE ws.ws_sold_date_sk = fd.d_date_sk) AND EXISTS (SELECT 1 FROM filtered_items fi WHERE ws.ws_item_sk = fi.i_item_sk) AND ws.ws_sales_price / ws.ws_list_price BETWEEN 0.65 AND 0.75 UNION ALL SELECT cs_ship_customer_sk AS customer_sk FROM catalog_sales cs WHERE EXISTS (SELECT 1 FROM filtered_dates fd WHERE cs.cs_sold_date_sk = fd.d_date_sk) AND EXISTS (SELECT 1 FROM filtered_items fi WHERE cs.cs_item_sk = fi.i_item_sk) AND cs.cs_sales_price / cs.cs_list_price BETWEEN 0.65 AND 0.75), customer_base AS (SELECT cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating, cd.cd_dep_count, cd.cd_dep_employed_count, cd.cd_dep_college_count FROM customer c INNER JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk INNER JOIN customer_demographics cd ON cd.cd_demo_sk = c.c_current_cdemo_sk INNER JOIN sales_consolidated sc ON c.c_customer_sk = sc.customer_sk WHERE ca.ca_county IN ('Alameda County', 'Lexington city', 'Pender County', 'Petroleum County', 'Walworth County') AND c.c_birth_month IN (4, 5) AND cd.cd_marital_status IN ('U', 'S', 'U') AND cd.cd_education_status IN ('College', 'Secondary', 'College') AND cd.cd_gender = 'M') SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3, cd_dep_count, COUNT(*) AS cnt4, cd_dep_employed_count, COUNT(*) AS cnt5, cd_dep_college_count, COUNT(*) AS cnt6 FROM customer_base GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100