WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), store_filter AS (SELECT s_store_sk FROM store), hdemo_filter AS (SELECT hd_demo_sk, hd_dep_count FROM household_demographics WHERE hd_dep_count IN (1, 3)), cdemo_filter AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE (cd_marital_status = 'M' AND cd_education_status = '2 yr Degree') OR (cd_marital_status = 'U' AND cd_education_status = 'College') OR (cd_marital_status = 'S' AND cd_education_status = 'Unknown')), addr_filter AS (SELECT ca_address_sk, ca_state FROM customer_address WHERE ca_country = 'United States' AND ca_state IN ('CO', 'NC', 'TX', 'AR', 'NY', 'IA', 'IL')), fact_join AS (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM store_sales INNER JOIN date_filter ON ss_sold_date_sk = date_filter.d_date_sk INNER JOIN store_filter ON ss_store_sk = store_filter.s_store_sk INNER JOIN hdemo_filter ON ss_hdemo_sk = hdemo_filter.hd_demo_sk INNER JOIN cdemo_filter ON ss_cdemo_sk = cdemo_filter.cd_demo_sk INNER JOIN addr_filter ON ss_addr_sk = addr_filter.ca_address_sk WHERE ((cd_marital_status = 'M' AND cd_education_status = '2 yr Degree' AND ss_sales_price BETWEEN 100.00 AND 150.00 AND hd_dep_count = 3) OR (cd_marital_status = 'U' AND cd_education_status = 'College' AND ss_sales_price BETWEEN 50.00 AND 100.00 AND hd_dep_count = 1) OR (cd_marital_status = 'S' AND cd_education_status = 'Unknown' AND ss_sales_price BETWEEN 150.00 AND 200.00 AND hd_dep_count = 1)) AND ((ca_state IN ('CO', 'NC', 'TX') AND ss_net_profit BETWEEN 100 AND 200) OR (ca_state IN ('AR', 'NY', 'TX') AND ss_net_profit BETWEEN 150 AND 300) OR (ca_state IN ('IA', 'IL', 'NC') AND ss_net_profit BETWEEN 50 AND 250))) SELECT AVG(ss_quantity), AVG(ss_ext_sales_price), AVG(ss_ext_wholesale_cost), SUM(ss_ext_wholesale_cost) FROM fact_join