WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), hd_cte AS (SELECT hd_demo_sk FROM household_demographics WHERE (hd_dep_count = 3) OR (hd_dep_count = 1)), cd_cte AS (SELECT cd_demo_sk FROM customer_demographics WHERE ((cd_marital_status = 'M' AND cd_education_status = '2 yr Degree') OR (cd_marital_status = 'S' AND cd_education_status = 'Primary') OR (cd_marital_status = 'W' AND cd_education_status = 'Primary'))), ca_cte AS (SELECT ca_address_sk FROM customer_address WHERE ca_country = 'United States' AND ca_state IN ('GA', 'KY', 'SD', 'AR', 'IN', 'VA', 'KS', 'OH', 'SD')), store_cte AS (SELECT s_store_sk FROM store), fact_filtered AS (SELECT ss.ss_quantity, ss.ss_ext_sales_price, ss.ss_ext_wholesale_cost, ss.ss_net_profit, ca.ca_state FROM store_sales ss INNER JOIN date_cte d ON ss.ss_sold_date_sk = d.d_date_sk INNER JOIN hd_cte hd ON ss.ss_hdemo_sk = hd.hd_demo_sk INNER JOIN cd_cte cd ON ss.ss_cdemo_sk = cd.cd_demo_sk INNER JOIN ca_cte ca ON ss.ss_addr_sk = ca.ca_address_sk INNER JOIN store_cte s ON ss.ss_store_sk = s.s_store_sk WHERE ((ss.ss_sales_price BETWEEN 100.00 AND 150.00) OR (ss.ss_sales_price BETWEEN 50.00 AND 100.00) OR (ss.ss_sales_price BETWEEN 150.00 AND 200.00))), union_branches AS ((SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM fact_filtered WHERE ca_state IN ('GA','KY','SD') AND ss_net_profit BETWEEN 100 AND 200) UNION ALL (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM fact_filtered WHERE ca_state IN ('AR','IN','VA') AND ss_net_profit BETWEEN 150 AND 300) UNION ALL (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM fact_filtered WHERE ca_state IN ('KS','OH','SD') AND ss_net_profit BETWEEN 50 AND 250)) SELECT MIN(ss_quantity), MIN(ss_ext_sales_price), MIN(ss_ext_wholesale_cost), MIN(ss_ext_wholesale_cost) FROM union_branches