WITH filtered_date AS (SELECT d_date_sk, d_year, d_week_seq FROM date_dim WHERE d_year BETWEEN 1999 AND 2001), filtered_item AS (SELECT i_item_sk, i_brand_id, i_class_id, i_category_id FROM item WHERE i_category IN ('Electronics', 'Jewelry', 'Men') AND i_manager_id BETWEEN 91 AND 100), prefactored_sales AS (SELECT ss_item_sk, ss_quantity, ss_list_price, ss_sold_date_sk FROM store_sales JOIN filtered_date ON ss_sold_date_sk = d_date_sk WHERE ss_wholesale_cost BETWEEN 35 AND 55), cross_items AS (SELECT i_item_sk AS ss_item_sk FROM filtered_item WHERE (i_brand_id, i_class_id, i_category_id) IN ( (SELECT i_brand_id, i_class_id, i_category_id FROM prefactored_sales JOIN filtered_item ON ss_item_sk = i_item_sk) INTERSECT (SELECT i_brand_id, i_class_id, i_category_id FROM catalog_sales JOIN filtered_item ON cs_item_sk = i_item_sk JOIN filtered_date ON cs_sold_date_sk = d_date_sk WHERE cs_wholesale_cost BETWEEN 35 AND 55) INTERSECT (SELECT i_brand_id, i_class_id, i_category_id FROM web_sales JOIN filtered_item ON ws_item_sk = i_item_sk JOIN filtered_date ON ws_sold_date_sk = d_date_sk WHERE ws_wholesale_cost BETWEEN 35 AND 55) )), avg_sales AS (SELECT AVG(quantity * list_price) AS average_sales FROM ( SELECT ss_quantity AS quantity, ss_list_price AS list_price FROM prefactored_sales UNION ALL SELECT cs_quantity AS quantity, cs_list_price AS list_price FROM catalog_sales JOIN filtered_date ON cs_sold_date_sk = d_date_sk WHERE cs_wholesale_cost BETWEEN 35 AND 55 UNION ALL SELECT ws_quantity AS quantity, ws_list_price AS list_price FROM web_sales JOIN filtered_date ON ws_sold_date_sk = d_date_sk WHERE ws_wholesale_cost BETWEEN 35 AND 55 ) AS x) SELECT this_year.channel AS ty_channel, this_year.i_brand_id AS ty_brand, this_year.i_class_id AS ty_class, this_year.i_category_id AS ty_category, this_year.sales AS ty_sales, this_year.number_sales AS ty_number_sales, last_year.channel AS ly_channel, last_year.i_brand_id AS ly_brand, last_year.i_class_id AS ly_class, last_year.i_category_id AS ly_category, last_year.sales AS ly_sales, last_year.number_sales AS ly_number_sales FROM ( SELECT 'store' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ss_quantity * ss_list_price) AS sales, COUNT(*) AS number_sales FROM store_sales JOIN filtered_item ON ss_item_sk = i_item_sk JOIN filtered_date ON ss_sold_date_sk = d_date_sk JOIN cross_items ON ss_item_sk = cross_items.ss_item_sk WHERE d_week_seq = (SELECT d_week_seq FROM filtered_date WHERE d_year = 1999 + 1 AND d_moy = 12 AND d_dom = 20) AND i_category IN ('Electronics', 'Jewelry', 'Men') AND i_manager_id BETWEEN 91 AND 100 AND ss_wholesale_cost BETWEEN 35 AND 55 GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ss_quantity * ss_list_price) > (SELECT average_sales FROM avg_sales) ) AS this_year JOIN ( SELECT 'store' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ss_quantity * ss_list_price) AS sales, COUNT(*) AS number_sales FROM store_sales JOIN filtered_item ON ss_item_sk = i_item_sk JOIN filtered_date ON ss_sold_date_sk = d_date_sk JOIN cross_items ON ss_item_sk = cross_items.ss_item_sk WHERE d_week_seq = (SELECT d_week_seq FROM filtered_date WHERE d_year = 1999 AND d_moy = 12 AND d_dom = 20) AND i_category IN ('Electronics', 'Jewelry', 'Men') AND i_manager_id BETWEEN 91 AND 100 AND ss_wholesale_cost BETWEEN 35 AND 55 GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ss_quantity * ss_list_price) > (SELECT average_sales FROM avg_sales) ) AS last_year ON this_year.i_brand_id = last_year.i_brand_id AND this_year.i_class_id = last_year.i_class_id AND this_year.i_category_id = last_year.i_category_id ORDER BY this_year.channel, this_year.i_brand_id, this_year.i_class_id, this_year.i_category_id LIMIT 100