WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), item_cte AS (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Sports'), cd_cte AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'College'), customer_cte AS (SELECT c_customer_sk, c_birth_year FROM customer WHERE c_birth_month = 2), filtered_sales AS (SELECT i.i_item_id, c.c_customer_sk, c.c_birth_year, cs.cs_quantity, cs.cs_list_price, cs.cs_coupon_amt, cs.cs_sales_price, cs.cs_net_profit FROM catalog_sales cs INNER JOIN date_cte d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN item_cte i ON cs.cs_item_sk = i.i_item_sk INNER JOIN cd_cte cd ON cs.cs_bill_cdemo_sk = cd.cd_demo_sk INNER JOIN customer_cte c ON cs.cs_bill_customer_sk = c.c_customer_sk WHERE cs.cs_wholesale_cost BETWEEN 41 AND 46), aggregate_partial AS (SELECT i_item_id, c_customer_sk, c_birth_year, AVG(CAST(cs_quantity AS DECIMAL(12,2))) AS agg1, AVG(CAST(cs_list_price AS DECIMAL(12,2))) AS agg2, AVG(CAST(cs_coupon_amt AS DECIMAL(12,2))) AS agg3, AVG(CAST(cs_sales_price AS DECIMAL(12,2))) AS agg4, AVG(CAST(cs_net_profit AS DECIMAL(12,2))) AS agg5, AVG(CAST(c_birth_year AS DECIMAL(12,2))) AS agg6 FROM filtered_sales GROUP BY i_item_id, c_customer_sk, c_birth_year), ca_cte AS (SELECT ca_address_sk, ca_country, ca_state, ca_county FROM customer_address WHERE ca_state IN ('AR', 'NY', 'TX')), join_ca AS (SELECT ap.i_item_id, ca.ca_country, ca.ca_state, ca.ca_county, ap.agg1, ap.agg2, ap.agg3, ap.agg4, ap.agg5, ap.agg6 FROM aggregate_partial ap INNER JOIN customer cust ON ap.c_customer_sk = cust.c_customer_sk INNER JOIN ca_cte ca ON cust.c_current_addr_sk = ca.ca_address_sk), final_aggregate AS (SELECT i_item_id, ca_country, ca_state, ca_county, AVG(agg1) AS agg1, AVG(agg2) AS agg2, AVG(agg3) AS agg3, AVG(agg4) AS agg4, AVG(agg5) AS agg5, AVG(agg6) AS agg6 FROM join_ca GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county)) SELECT i_item_id, ca_country, ca_state, ca_county, agg1, agg2, agg3, agg4, agg5, agg6 FROM final_aggregate ORDER BY ca_country, ca_state, ca_county, i_item_id LIMIT 100