WITH filtered_catalog_sales AS (SELECT cs.cs_quantity, cs.cs_list_price, cs.cs_coupon_amt, cs.cs_sales_price, cs.cs_net_profit, i.i_item_id, c.c_birth_year, c.c_current_addr_sk FROM catalog_sales cs JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk AND d.d_year = 1999 JOIN item i ON cs.cs_item_sk = i.i_item_sk AND i.i_category = 'Music' JOIN customer_demographics cd ON cs.cs_bill_cdemo_sk = cd.cd_demo_sk AND cd.cd_gender = 'F' AND cd.cd_education_status = '4 yr Degree' JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk AND c.c_birth_month = 12 WHERE cs.cs_wholesale_cost BETWEEN 5 AND 10), join_with_dims AS (SELECT fcs.i_item_id, ca.ca_country, ca.ca_state, ca.ca_county, fcs.cs_quantity, fcs.cs_list_price, fcs.cs_coupon_amt, fcs.cs_sales_price, fcs.cs_net_profit, fcs.c_birth_year FROM filtered_catalog_sales fcs JOIN customer_address ca ON fcs.c_current_addr_sk = ca.ca_address_sk AND ca.ca_state IN ('AR', 'IN', 'VA')), aggregate AS (SELECT i_item_id, ca_country, ca_state, ca_county, AVG(CAST(cs_quantity AS DECIMAL(12,2))) AS agg1, AVG(CAST(cs_list_price AS DECIMAL(12,2))) AS agg2, AVG(CAST(cs_coupon_amt AS DECIMAL(12,2))) AS agg3, AVG(CAST(cs_sales_price AS DECIMAL(12,2))) AS agg4, AVG(CAST(cs_net_profit AS DECIMAL(12,2))) AS agg5, AVG(CAST(c_birth_year AS DECIMAL(12,2))) AS agg6 FROM join_with_dims GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county)) SELECT i_item_id, ca_country, ca_state, ca_county, agg1, agg2, agg3, agg4, agg5, agg6 FROM aggregate ORDER BY ca_country, ca_state, ca_county, i_item_id LIMIT 100