SELECT i_item_id, ca_country, ca_state, ca_county, AVG(CAST(cs_quantity AS decimal(12,2))) AS agg1, AVG(CAST(cs_list_price AS decimal(12,2))) AS agg2, AVG(CAST(cs_coupon_amt AS decimal(12,2))) AS agg3, AVG(CAST(cs_sales_price AS decimal(12,2))) AS agg4, AVG(CAST(cs_net_profit AS decimal(12,2))) AS agg5, AVG(CAST(c_birth_year AS decimal(12,2))) AS agg6 FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk JOIN item ON cs_item_sk = i_item_sk JOIN customer_demographics ON cs_bill_cdemo_sk = cd_demo_sk JOIN customer ON cs_bill_customer_sk = c_customer_sk JOIN customer_address ON c_current_addr_sk = ca_address_sk WHERE cd_gender = 'F' AND cd_education_status = '4 yr Degree' AND d_year = 1999 AND c_birth_month = 12 AND ca_state IN ('AR', 'IN', 'VA') AND cs_wholesale_cost BETWEEN 5 AND 10 AND i_category = 'Music' GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county) ORDER BY ca_country, ca_state, ca_county, i_item_id LIMIT 100