WITH date_filtered AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 1999), item_filtered AS (SELECT i_item_sk, i_item_desc FROM item WHERE i_manager_id BETWEEN 44 AND 63 AND i_category IN ('Men', 'Music', 'Sports')), customer_filtered AS (SELECT c_customer_sk FROM customer WHERE c_birth_year BETWEEN 1987 AND 1993), store_sales_unified AS (SELECT ss.ss_item_sk, ss.ss_sold_date_sk, df.d_date, ift.i_item_desc, ss.ss_customer_sk, cf.c_customer_sk, ss.ss_quantity, ss.ss_sales_price, ss.ss_wholesale_cost FROM store_sales ss INNER JOIN date_filtered df ON ss.ss_sold_date_sk = df.d_date_sk LEFT JOIN item_filtered ift ON ss.ss_item_sk = ift.i_item_sk LEFT JOIN customer_filtered cf ON ss.ss_customer_sk = cf.c_customer_sk), max_store_sales AS (SELECT ss_customer_sk, SUM(ss_quantity * ss_sales_price) AS csales FROM store_sales_unified WHERE ss_wholesale_cost BETWEEN 26 AND 36 GROUP BY ss_customer_sk), best_ss_customer_joined AS (SELECT ssu.ss_customer_sk AS c_customer_sk, SUM(ssu.ss_quantity * ssu.ss_sales_price) AS ssales FROM store_sales_unified ssu INNER JOIN max_store_sales mss ON ssu.ss_customer_sk = mss.ss_customer_sk WHERE ssu.c_customer_sk IS NOT NULL GROUP BY ssu.ss_customer_sk, mss.csales HAVING SUM(ssu.ss_quantity * ssu.ss_sales_price) > (95/100.0) * mss.csales), frequent_ss_items AS (SELECT SUBSTRING(ssu.i_item_desc FROM 1 FOR 30) AS itemdesc, ssu.ss_item_sk AS item_sk, ssu.d_date AS solddate, COUNT(*) AS cnt FROM store_sales_unified ssu INNER JOIN item_filtered ift ON ssu.ss_item_sk = ift.i_item_sk GROUP BY SUBSTRING(ssu.i_item_desc FROM 1 FOR 30), ssu.ss_item_sk, ssu.d_date HAVING COUNT(*) > 4) SELECT SUM(sales) FROM (SELECT cs_quantity * cs_list_price AS sales FROM catalog_sales INNER JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE d_year = 1999 AND d_moy = 5 AND cs_item_sk IN (SELECT item_sk FROM frequent_ss_items) AND cs_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer_joined) AND cs_wholesale_cost BETWEEN 26 AND 36 UNION ALL SELECT ws_quantity * ws_list_price AS sales FROM web_sales INNER JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE d_year = 1999 AND d_moy = 5 AND ws_item_sk IN (SELECT item_sk FROM frequent_ss_items) AND ws_bill_customer_sk IN (SELECT c_customer_sk FROM best_ss_customer_joined) AND ws_wholesale_cost BETWEEN 26 AND 36) tmp2 LIMIT 100