WITH store_sales_agg AS (SELECT ss_item_sk, ss_store_sk, MAX(ss_net_profit) AS store_sales_profit FROM store_sales JOIN date_dim d1 ON ss_sold_date_sk = d1.d_date_sk WHERE d1.d_moy = 3 AND d1.d_year = 1999 GROUP BY ss_item_sk, ss_store_sk), store_returns_agg AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, MAX(sr_net_loss) AS store_returns_loss FROM store_returns JOIN date_dim d2 ON sr_returned_date_sk = d2.d_date_sk WHERE d2.d_moy BETWEEN 3 AND 5 AND d2.d_year = 1999 GROUP BY sr_item_sk, sr_customer_sk, sr_ticket_number), catalog_sales_agg AS (SELECT cs_item_sk, cs_bill_customer_sk, MAX(cs_net_profit) AS catalog_sales_profit FROM catalog_sales JOIN date_dim d3 ON cs_sold_date_sk = d3.d_date_sk WHERE d3.d_moy BETWEEN 3 AND 5 AND d3.d_year = 1999 GROUP BY cs_item_sk, cs_bill_customer_sk), join_aggregates AS (SELECT ss.ss_item_sk, ss.ss_store_sk, ss.store_sales_profit, sr.store_returns_loss, cs.catalog_sales_profit FROM store_sales_agg ss JOIN store_returns_agg sr ON ss.ss_item_sk = sr.sr_item_sk JOIN catalog_sales_agg cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk), dimension_lookup AS (SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, agg.store_sales_profit, agg.store_returns_loss, agg.catalog_sales_profit FROM join_aggregates agg JOIN item i ON agg.ss_item_sk = i.i_item_sk JOIN store s ON agg.ss_store_sk = s.s_store_sk) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, store_sales_profit, store_returns_loss, catalog_sales_profit FROM dimension_lookup ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100