WITH fact_joins AS (SELECT ss.ss_item_sk, ss.ss_store_sk, ss.ss_net_profit, sr.sr_net_loss, cs.cs_net_profit FROM store_sales ss JOIN date_dim d1 ON ss.ss_sold_date_sk = d1.d_date_sk AND d1.d_moy = 3 AND d1.d_year = 1999 JOIN store_returns sr ON ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number JOIN date_dim d2 ON sr.sr_returned_date_sk = d2.d_date_sk AND d2.d_moy BETWEEN 3 AND 5 AND d2.d_year = 1999 JOIN catalog_sales cs ON sr.sr_customer_sk = cs.cs_bill_customer_sk AND sr.sr_item_sk = cs.cs_item_sk JOIN date_dim d3 ON cs.cs_sold_date_sk = d3.d_date_sk AND d3.d_moy BETWEEN 3 AND 5 AND d3.d_year = 1999), aggregation AS (SELECT ss_item_sk, ss_store_sk, MAX(ss_net_profit) AS store_sales_profit, MAX(sr_net_loss) AS store_returns_loss, MAX(cs_net_profit) AS catalog_sales_profit FROM fact_joins GROUP BY ss_item_sk, ss_store_sk), dimension_lookup AS (SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, agg.store_sales_profit, agg.store_returns_loss, agg.catalog_sales_profit FROM aggregation agg JOIN item i ON agg.ss_item_sk = i.i_item_sk JOIN store s ON agg.ss_store_sk = s.s_store_sk) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, store_sales_profit, store_returns_loss, catalog_sales_profit FROM dimension_lookup ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100