WITH d1_cte AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 3 AND d_year = 1999), d2_cte AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 3 AND 5 AND d_year = 1999), d3_cte AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 3 AND 5 AND d_year = 1999), store_sales_cte AS (SELECT ss_item_sk, ss_store_sk, ss_customer_sk, ss_ticket_number, ss_net_profit FROM store_sales INNER JOIN d1_cte ON ss_sold_date_sk = d1_cte.d_date_sk), store_returns_cte AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, sr_net_loss FROM store_returns INNER JOIN d2_cte ON sr_returned_date_sk = d2_cte.d_date_sk), catalog_sales_cte AS (SELECT cs_item_sk, cs_bill_customer_sk, cs_net_profit FROM catalog_sales INNER JOIN d3_cte ON cs_sold_date_sk = d3_cte.d_date_sk), fact_joins_parallel_cte AS (SELECT ss.ss_item_sk, ss.ss_store_sk, ss.ss_net_profit, sr.sr_net_loss, cs.cs_net_profit FROM store_sales_cte ss INNER JOIN store_returns_cte sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_ticket_number = sr.sr_ticket_number INNER JOIN catalog_sales_cte cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk), aggregation_cte AS (SELECT ss_item_sk, ss_store_sk, MAX(ss_net_profit) AS store_sales_profit, MAX(sr_net_loss) AS store_returns_loss, MAX(cs_net_profit) AS catalog_sales_profit FROM fact_joins_parallel_cte GROUP BY ss_item_sk, ss_store_sk), dimension_lookup_cte AS (SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, agg.store_sales_profit, agg.store_returns_loss, agg.catalog_sales_profit FROM aggregation_cte agg INNER JOIN item i ON agg.ss_item_sk = i.i_item_sk INNER JOIN store s ON agg.ss_store_sk = s.s_store_sk) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, store_sales_profit, store_returns_loss, catalog_sales_profit FROM dimension_lookup_cte ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100