WITH all_dates AS (SELECT d_date_sk, d_moy FROM date_dim WHERE d_year = 2000 AND ((d_moy = 2) OR (d_moy BETWEEN 2 AND 4))), filtered_store AS (SELECT s_store_sk, s_store_id, s_store_name FROM store), filtered_item AS (SELECT i_item_sk, i_item_id, i_item_desc FROM item), fact_joins AS (SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, ss.ss_net_profit, sr.sr_net_loss, cs.cs_net_profit FROM store_sales ss INNER JOIN all_dates d1 ON ss.ss_sold_date_sk = d1.d_date_sk AND d1.d_moy = 2 INNER JOIN filtered_store s ON s.s_store_sk = ss.ss_store_sk INNER JOIN filtered_item i ON i.i_item_sk = ss.ss_item_sk INNER JOIN store_returns sr ON ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number INNER JOIN all_dates d2 ON sr.sr_returned_date_sk = d2.d_date_sk AND d2.d_moy BETWEEN 2 AND 4 INNER JOIN catalog_sales cs ON sr.sr_customer_sk = cs.cs_bill_customer_sk AND sr.sr_item_sk = cs.cs_item_sk INNER JOIN all_dates d3 ON cs.cs_sold_date_sk = d3.d_date_sk AND d3.d_moy BETWEEN 2 AND 4) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, STDDEV_SAMP(ss_net_profit) AS store_sales_profit, STDDEV_SAMP(sr_net_loss) AS store_returns_loss, STDDEV_SAMP(cs_net_profit) AS catalog_sales_profit FROM fact_joins GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100