WITH filtered_d1 AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 2 AND d_year = 2000), filtered_d2 AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 2 AND 4 AND d_year = 2000), filtered_d3 AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 2 AND 4 AND d_year = 2000), store_sales_lateral AS (SELECT store_sales.ss_item_sk, store_sales.ss_customer_sk, store_sales.ss_ticket_number, store_sales.ss_store_sk, store_sales.ss_net_profit, i_item_id, i_item_desc, s_store_id, s_store_name FROM filtered_d1 JOIN LATERAL (SELECT ss_item_sk, ss_customer_sk, ss_ticket_number, ss_store_sk, ss_net_profit FROM store_sales WHERE ss_sold_date_sk = filtered_d1.d_date_sk) AS store_sales ON TRUE JOIN store ON s_store_sk = store_sales.ss_store_sk JOIN item ON i_item_sk = store_sales.ss_item_sk), store_returns_lateral AS (SELECT store_sales_lateral.*, store_returns.sr_net_loss FROM store_sales_lateral JOIN LATERAL (SELECT sr_net_loss FROM store_returns WHERE sr_customer_sk = store_sales_lateral.ss_customer_sk AND sr_item_sk = store_sales_lateral.ss_item_sk AND sr_ticket_number = store_sales_lateral.ss_ticket_number AND sr_returned_date_sk IN (SELECT d_date_sk FROM filtered_d2)) AS store_returns ON TRUE), catalog_sales_lateral AS (SELECT i_item_id, i_item_desc, s_store_id, s_store_name, ss_net_profit, sr_net_loss, cs_net_profit FROM store_returns_lateral JOIN LATERAL (SELECT cs_net_profit FROM catalog_sales WHERE cs_bill_customer_sk = store_returns_lateral.ss_customer_sk AND cs_item_sk = store_returns_lateral.ss_item_sk AND cs_sold_date_sk IN (SELECT d_date_sk FROM filtered_d3)) AS catalog_sales ON TRUE) SELECT i_item_id, i_item_desc, s_store_id, s_store_name, STDDEV_SAMP(ss_net_profit) AS store_sales_profit, STDDEV_SAMP(sr_net_loss) AS store_returns_loss, STDDEV_SAMP(cs_net_profit) AS catalog_sales_profit FROM catalog_sales_lateral GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100