WITH fact_filtered AS (SELECT ss_item_sk, ss_store_sk, ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price FROM store_sales WHERE EXISTS (SELECT 1 FROM date_dim WHERE d_date_sk = store_sales.ss_sold_date_sk AND d_year = 2002) AND EXISTS (SELECT 1 FROM customer_demographics WHERE cd_demo_sk = store_sales.ss_cdemo_sk AND cd_gender = 'F' AND cd_marital_status = 'M' AND cd_education_status = 'Primary')), joined_store_item AS (SELECT i.i_item_id, s.s_state, f.ss_quantity, f.ss_list_price, f.ss_coupon_amt, f.ss_sales_price FROM fact_filtered f INNER JOIN store s ON f.ss_store_sk = s.s_store_sk AND s.s_state = 'IA' INNER JOIN item i ON f.ss_item_sk = i.i_item_sk AND i.i_category = 'Sports') SELECT i_item_id, s_state, GROUPING(s_state) AS g_state, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM joined_store_item GROUP BY ROLLUP (i_item_id, s_state) ORDER BY i_item_id, s_state LIMIT 100