WITH date_cte AS MATERIALIZED (SELECT d_date_sk FROM date_dim WHERE d_year = 1999), store_cte AS MATERIALIZED (SELECT s_store_sk, s_state FROM store WHERE s_state = 'OH'), item_cte AS MATERIALIZED (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Music'), cd_cte AS MATERIALIZED (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'W' AND cd_education_status = 'Secondary'), fact_join AS (SELECT ss.i_item_id, st.s_state, ss.ss_quantity, ss.ss_list_price, ss.ss_coupon_amt, ss.ss_sales_price FROM store_sales ss INNER JOIN date_cte dt ON ss.ss_sold_date_sk = dt.d_date_sk INNER JOIN store_cte st ON ss.ss_store_sk = st.s_store_sk INNER JOIN item_cte it ON ss.ss_item_sk = it.i_item_sk INNER JOIN cd_cte cd ON ss.ss_cdemo_sk = cd.cd_demo_sk) SELECT i_item_id, s_state, GROUPING(s_state) AS g_state, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM fact_join GROUP BY ROLLUP (i_item_id, s_state) ORDER BY i_item_id, s_state LIMIT 100