WITH date_cte AS MATERIALIZED (SELECT d_date_sk FROM date_dim WHERE d_year = 1999), store_cte AS MATERIALIZED (SELECT s_store_sk, s_state FROM store WHERE s_state = 'OH'), item_cte AS MATERIALIZED (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Music'), cd_cte AS MATERIALIZED (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'W' AND cd_education_status = 'Secondary') SELECT i_item_id, s_state, grouping(s_state) g_state, avg(ss_quantity) agg1, avg(ss_list_price) agg2, avg(ss_coupon_amt) agg3, avg(ss_sales_price) agg4 FROM store_cte INNER JOIN store_sales ON store_sales.ss_store_sk = store_cte.s_store_sk INNER JOIN date_cte ON store_sales.ss_sold_date_sk = date_cte.d_date_sk INNER JOIN item_cte ON store_sales.ss_item_sk = item_cte.i_item_sk INNER JOIN cd_cte ON store_sales.ss_cdemo_sk = cd_cte.cd_demo_sk GROUP BY ROLLUP (i_item_id, s_state) ORDER BY i_item_id, s_state LIMIT 100