WITH base_cte AS (SELECT wr_returning_customer_sk AS ctr_customer_sk, ca_state AS ctr_state, wr_reason_sk AS ctr_reason_sk, SUM(wr_return_amt) AS ctr_total_return FROM web_returns, date_dim, customer_address, item WHERE wr_returned_date_sk = d_date_sk AND d_year = 2002 AND wr_returning_addr_sk = ca_address_sk AND wr_item_sk = i_item_sk AND i_manager_id BETWEEN 68 AND 77 AND wr_return_amt / wr_return_quantity BETWEEN 242 AND 271 GROUP BY wr_returning_customer_sk, ca_state, wr_reason_sk), state_avg_cte AS (SELECT ctr_state, AVG(ctr_total_return) * 1.2 AS state_avg_threshold FROM base_cte GROUP BY ctr_state) SELECT c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr1.ctr_total_return FROM base_cte ctr1 INNER JOIN state_avg_cte ON ctr1.ctr_state = state_avg_cte.ctr_state INNER JOIN customer ON ctr1.ctr_customer_sk = c_customer_sk INNER JOIN customer_address ON ca_address_sk = c_current_addr_sk WHERE ctr1.ctr_total_return > state_avg_cte.state_avg_threshold AND ctr1.ctr_reason_sk IN (20, 36) AND c_birth_year BETWEEN 1985 AND 1991 AND customer_address.ca_state IN ('OK','SC','TX','WI') ORDER BY c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr1.ctr_total_return LIMIT 100