WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), filtered_items AS (SELECT i_item_sk FROM item WHERE i_manager_id BETWEEN 68 AND 77), filtered_addresses_current AS (SELECT ca_address_sk FROM customer_address WHERE ca_state IN ('OK', 'SC', 'TX', 'WI')), filtered_addresses_returning AS (SELECT ca_address_sk, ca_state FROM customer_address), web_returns_joined AS (SELECT wr_returning_customer_sk, ca_state, wr_reason_sk, wr_return_amt FROM web_returns INNER JOIN filtered_dates ON wr_returned_date_sk = filtered_dates.d_date_sk INNER JOIN filtered_addresses_returning ON wr_returning_addr_sk = filtered_addresses_returning.ca_address_sk INNER JOIN filtered_items ON wr_item_sk = filtered_items.i_item_sk WHERE wr_return_amt / wr_return_quantity BETWEEN 242 AND 271), cte_aggregated AS (SELECT wr_returning_customer_sk AS ctr_customer_sk, ca_state AS ctr_state, wr_reason_sk AS ctr_reason_sk, SUM(wr_return_amt) AS ctr_total_return FROM web_returns_joined GROUP BY wr_returning_customer_sk, ca_state, wr_reason_sk), state_avg AS (SELECT ctr_state, AVG(ctr_total_return) * 1.2 AS state_avg_threshold FROM cte_aggregated GROUP BY ctr_state)
SELECT c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr_total_return FROM cte_aggregated ctr1 INNER JOIN state_avg ON ctr1.ctr_state = state_avg.ctr_state INNER JOIN customer ON ctr1.ctr_customer_sk = customer.c_customer_sk INNER JOIN filtered_addresses_current ON filtered_addresses_current.ca_address_sk = customer.c_current_addr_sk WHERE ctr1.ctr_total_return > state_avg.state_avg_threshold AND ctr1.ctr_reason_sk IN (20, 36) AND customer.c_birth_year BETWEEN 1985 AND 1991 ORDER BY c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr_total_return LIMIT 100