WITH store_sales_base AS (SELECT DISTINCT c.c_last_name, c.c_first_name, d.d_date FROM store_sales ss INNER JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk INNER JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk WHERE d.d_month_seq BETWEEN 1200 AND 1211 AND c.c_birth_month IN (1, 2, 5, 12) AND ss.ss_list_price BETWEEN 193 AND 252 AND ss.ss_wholesale_cost BETWEEN 35 AND 55) SELECT COUNT(*) FROM store_sales_base ssb WHERE EXISTS (SELECT 1 FROM catalog_sales cs INNER JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk WHERE d.d_month_seq BETWEEN 1200 AND 1211 AND c.c_birth_month IN (1, 2, 5, 12) AND cs.cs_list_price BETWEEN 193 AND 252 AND cs.cs_wholesale_cost BETWEEN 35 AND 55 AND c.c_last_name = ssb.c_last_name AND c.c_first_name = ssb.c_first_name AND d.d_date = ssb.d_date) AND EXISTS (SELECT 1 FROM web_sales ws INNER JOIN date_dim d ON ws.ws_sold_date_sk = d.d_date_sk INNER JOIN customer c ON ws.ws_bill_customer_sk = c.c_customer_sk WHERE d.d_month_seq BETWEEN 1200 AND 1211 AND c.c_birth_month IN (1, 2, 5, 12) AND ws.ws_list_price BETWEEN 193 AND 252 AND ws.ws_wholesale_cost BETWEEN 35 AND 55 AND c.c_last_name = ssb.c_last_name AND c.c_first_name = ssb.c_first_name AND d.d_date = ssb.d_date) LIMIT 100