WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1200 + 11), filtered_customers AS (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_month IN (1, 2, 5, 12)), store_subquery AS (SELECT DISTINCT c.c_last_name, c.c_first_name, d.d_date FROM store_sales ss INNER JOIN filtered_dates d ON ss.ss_sold_date_sk = d.d_date_sk INNER JOIN filtered_customers c ON ss.ss_customer_sk = c.c_customer_sk WHERE ss.ss_list_price BETWEEN 193 AND 252 AND ss.ss_wholesale_cost BETWEEN 35 AND 55), catalog_subquery AS (SELECT DISTINCT c.c_last_name, c.c_first_name, d.d_date FROM catalog_sales cs INNER JOIN filtered_dates d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN filtered_customers c ON cs.cs_bill_customer_sk = c.c_customer_sk WHERE cs.cs_list_price BETWEEN 193 AND 252 AND cs.cs_wholesale_cost BETWEEN 35 AND 55), web_subquery AS (SELECT DISTINCT c.c_last_name, c.c_first_name, d.d_date FROM web_sales ws INNER JOIN filtered_dates d ON ws.ws_sold_date_sk = d.d_date_sk INNER JOIN filtered_customers c ON ws.ws_bill_customer_sk = c.c_customer_sk WHERE ws.ws_list_price BETWEEN 193 AND 252 AND ws.ws_wholesale_cost BETWEEN 35 AND 55) SELECT COUNT(*) FROM (SELECT * FROM store_subquery INTERSECT SELECT * FROM catalog_subquery INTERSECT SELECT * FROM web_subquery) hot_cust LIMIT 100