SELECT COUNT(*) FROM (SELECT c_last_name, c_first_name, d_date FROM (SELECT DISTINCT 1 AS channel, c.c_last_name, c.c_first_name, dd.d_date FROM store_sales ss JOIN (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1207 AND 1207 + 11) dd ON ss.ss_sold_date_sk = dd.d_date_sk JOIN (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_month IN (2, 3, 4, 10)) c ON ss.ss_customer_sk = c.c_customer_sk WHERE ss.ss_list_price BETWEEN 241 AND 300 AND ss.ss_wholesale_cost BETWEEN 76 AND 96 UNION ALL SELECT DISTINCT 2 AS channel, c.c_last_name, c.c_first_name, dd.d_date FROM catalog_sales cs JOIN (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1207 AND 1207 + 11) dd ON cs.cs_sold_date_sk = dd.d_date_sk JOIN (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_month IN (2, 3, 4, 10)) c ON cs.cs_bill_customer_sk = c.c_customer_sk WHERE cs.cs_list_price BETWEEN 241 AND 300 AND cs.cs_wholesale_cost BETWEEN 76 AND 96 UNION ALL SELECT DISTINCT 3 AS channel, c.c_last_name, c.c_first_name, dd.d_date FROM web_sales ws JOIN (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1207 AND 1207 + 11) dd ON ws.ws_sold_date_sk = dd.d_date_sk JOIN (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_month IN (2, 3, 4, 10)) c ON ws.ws_bill_customer_sk = c.c_customer_sk WHERE ws.ws_list_price BETWEEN 241 AND 300 AND ws.ws_wholesale_cost BETWEEN 76 AND 96) all_channels GROUP BY c_last_name, c_first_name, d_date HAVING COUNT(DISTINCT channel) = 3) hot_cust LIMIT 100