WITH date_cte AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-19' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-19' AS DATE) + INTERVAL '30 DAY')), item_cte AS (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Jewelry' AND i_manager_id BETWEEN 61 AND 100), returns_cte AS (SELECT cr_order_number, cr_item_sk, cr_refunded_cash FROM catalog_returns WHERE cr_reason_sk = 33) SELECT w_state, i_item_id, SUM(CASE WHEN (d_date < CAST('2002-02-19' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash,0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (d_date >= CAST('2002-02-19' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash,0) ELSE 0 END) AS sales_after FROM catalog_sales INNER JOIN date_cte ON cs_sold_date_sk = date_cte.d_date_sk INNER JOIN item_cte ON cs_item_sk = item_cte.i_item_sk LEFT JOIN returns_cte ON cs_order_number = returns_cte.cr_order_number AND cs_item_sk = returns_cte.cr_item_sk INNER JOIN warehouse ON cs_warehouse_sk = w_warehouse_sk WHERE cs_wholesale_cost BETWEEN 42 AND 61 GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100