WITH date_filter AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-20' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-20' AS DATE) + INTERVAL '30 DAY')), item_filter AS (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Shoes' AND i_manager_id BETWEEN 42 AND 81) SELECT w.w_state, i.i_item_id, SUM(CASE WHEN (CAST(d.d_date AS DATE) < CAST('2002-02-20' AS DATE)) THEN cs.cs_sales_price - COALESCE(cr.cr_refunded_cash,0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(d.d_date AS DATE) >= CAST('2002-02-20' AS DATE)) THEN cs.cs_sales_price - COALESCE(cr.cr_refunded_cash,0) ELSE 0 END) AS sales_after FROM catalog_sales cs INNER JOIN date_filter d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN item_filter i ON cs.cs_item_sk = i.i_item_sk INNER JOIN catalog_returns cr ON (cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk AND cr.cr_reason_sk = 40) INNER JOIN warehouse w ON cs.cs_warehouse_sk = w.w_warehouse_sk WHERE cs.cs_wholesale_cost BETWEEN 68 AND 87 GROUP BY w.w_state, i.i_item_id ORDER BY w.w_state, i.i_item_id LIMIT 100