WITH filtered_d2 AS (SELECT d_date_sk, d_date, (d_date - INTERVAL '120 day') AS window_start FROM date_dim WHERE d_year = 2000 AND d_moy = 8), d1_candidate_window AS (SELECT d1.d_date_sk AS d1_date_sk, d1.d_date AS d1_date, fd2.d_date_sk AS d2_date_sk FROM date_dim d1 JOIN filtered_d2 fd2 ON d1.d_date BETWEEN fd2.window_start AND fd2.d_date), store_returns_with_d2 AS (SELECT sr_ticket_number, sr_item_sk, sr_customer_sk, fd2.d_date_sk AS return_date_sk FROM store_returns JOIN filtered_d2 fd2 ON sr_returned_date_sk = fd2.d_date_sk), store_sales_with_dates AS (SELECT ss_store_sk, ss_sold_date_sk, sr.return_date_sk FROM store_sales JOIN store_returns_with_d2 sr ON ss_ticket_number = sr.sr_ticket_number AND ss_item_sk = sr.sr_item_sk AND ss_customer_sk = sr.sr_customer_sk JOIN d1_candidate_window d1w ON ss_sold_date_sk = d1w.d1_date_sk AND sr.return_date_sk = d1w.d2_date_sk), store_join AS (SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, ssd.ss_sold_date_sk, ssd.return_date_sk FROM store_sales_with_dates ssd JOIN store ON ssd.ss_store_sk = s_store_sk) SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, SUM(CASE WHEN (return_date_sk - ss_sold_date_sk <= 30) THEN 1 ELSE 0 END) AS "30 days", SUM(CASE WHEN (return_date_sk - ss_sold_date_sk > 30) AND (return_date_sk - ss_sold_date_sk <= 60) THEN 1 ELSE 0 END) AS "31-60 days", SUM(CASE WHEN (return_date_sk - ss_sold_date_sk > 60) AND (return_date_sk - ss_sold_date_sk <= 90) THEN 1 ELSE 0 END) AS "61-90 days", SUM(CASE WHEN (return_date_sk - ss_sold_date_sk > 90) AND (return_date_sk - ss_sold_date_sk <= 120) THEN 1 ELSE 0 END) AS "91-120 days", SUM(CASE WHEN (return_date_sk - ss_sold_date_sk > 120) THEN 1 ELSE 0 END) AS ">120 days" FROM store_join GROUP BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip ORDER BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip LIMIT 100