WITH d2_filtered AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 1999 AND d_moy = 7), store_returns_filtered AS (SELECT sr_item_sk, sr_ticket_number, sr_customer_sk, sr_returned_date_sk, d2.d_date FROM store_returns JOIN d2_filtered d2 ON sr_returned_date_sk = d2.d_date_sk), sales_returns_store_joined AS (SELECT s.s_store_name, s.s_company_id, s.s_street_number, s.s_street_name, s.s_street_type, s.s_suite_number, s.s_city, s.s_county, s.s_state, s.s_zip, ss.ss_sold_date_sk, sr.sr_returned_date_sk, sr.d_date FROM store_sales ss JOIN store_returns_filtered sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number AND ss.ss_customer_sk = sr.sr_customer_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk), d1_joined AS (SELECT j.s_store_name, j.s_company_id, j.s_street_number, j.s_street_name, j.s_street_type, j.s_suite_number, j.s_city, j.s_county, j.s_state, j.s_zip, j.ss_sold_date_sk, j.sr_returned_date_sk FROM sales_returns_store_joined j JOIN date_dim d1 ON j.ss_sold_date_sk = d1.d_date_sk WHERE d1.d_date BETWEEN (j.d_date - INTERVAL '120 DAY') AND j.d_date), aggregated AS (SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk <= 30) THEN 1 ELSE 0 END) AS "30 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 30) AND (sr_returned_date_sk - ss_sold_date_sk <= 60) THEN 1 ELSE 0 END) AS "31-60 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 60) AND (sr_returned_date_sk - ss_sold_date_sk <= 90) THEN 1 ELSE 0 END) AS "61-90 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 90) AND (sr_returned_date_sk - ss_sold_date_sk <= 120) THEN 1 ELSE 0 END) AS "91-120 days", SUM(CASE WHEN (sr_returned_date_sk - ss_sold_date_sk > 120) THEN 1 ELSE 0 END) AS ">120 days" FROM d1_joined GROUP BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip) SELECT s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip, "30 days", "31-60 days", "61-90 days", "91-120 days", ">120 days" FROM aggregated ORDER BY s_store_name, s_company_id, s_street_number, s_street_name, s_street_type, s_suite_number, s_city, s_county, s_state, s_zip LIMIT 100