WITH cs_ui AS (SELECT cs_item_sk, SUM(cs_ext_list_price) AS sale, SUM(cr_refunded_cash + cr_reversed_charge + cr_store_credit) AS refund FROM catalog_sales, catalog_returns WHERE cs_item_sk = cr_item_sk AND cs_order_number = cr_order_number AND cs_wholesale_cost BETWEEN 35 AND 55 GROUP BY cs_item_sk HAVING SUM(cs_ext_list_price) > 2 * SUM(cr_refunded_cash + cr_reversed_charge + cr_store_credit)), filtered_date_d1 AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year = 1998 OR d_year = 1999), filtered_date_d2 AS (SELECT d_date_sk, d_year FROM date_dim WHERE EXISTS (SELECT 1 FROM customer WHERE c_first_sales_date_sk = d_date_sk)), filtered_date_d3 AS (SELECT d_date_sk, d_year FROM date_dim WHERE EXISTS (SELECT 1 FROM customer WHERE c_first_shipto_date_sk = d_date_sk)), filtered_customer_address_ad2 AS (SELECT ca_address_sk, ca_street_number, ca_street_name, ca_city, ca_zip FROM customer_address WHERE ca_state IN ('IA','IL','TX')), filtered_promotion AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'N' AND p_channel_tv = 'Y' AND p_channel_radio = 'N'), filtered_cd AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_marital_status IN ('M','U') AND cd_education_status IN ('Unknown','Advanced Degree','College')), explicit_star_join AS (SELECT i_product_name, i_item_sk, s_store_name, s_zip, ad1.ca_street_number, ad1.ca_street_name, ad1.ca_city, ad1.ca_zip, ad2.ca_street_number, ad2.ca_street_name, ad2.ca_city, ad2.ca_zip, d1.d_year AS syear, d2.d_year AS fsyear, d3.d_year AS s2year, COUNT(*) AS cnt, SUM(ss_wholesale_cost) AS s1, SUM(ss_list_price) AS s2, SUM(ss_coupon_amt) AS s3 FROM store_sales INNER JOIN store_returns ON ss_item_sk = sr_item_sk AND ss_ticket_number = sr_ticket_number INNER JOIN cs_ui ON ss_item_sk = cs_ui.cs_item_sk INNER JOIN filtered_date_d1 d1 ON ss_sold_date_sk = d1.d_date_sk INNER JOIN store ON ss_store_sk = s_store_sk INNER JOIN customer ON ss_customer_sk = c_customer_sk INNER JOIN filtered_cd cd1 ON ss_cdemo_sk = cd1.cd_demo_sk INNER JOIN household_demographics hd1 ON ss_hdemo_sk = hd1.hd_demo_sk INNER JOIN income_band ib1 ON hd1.hd_income_band_sk = ib1.ib_income_band_sk INNER JOIN customer_address ad1 ON ss_addr_sk = ad1.ca_address_sk INNER JOIN filtered_cd cd2 ON c_current_cdemo_sk = cd2.cd_demo_sk INNER JOIN household_demographics hd2 ON c_current_hdemo_sk = hd2.hd_demo_sk INNER JOIN income_band ib2 ON hd2.hd_income_band_sk = ib2.ib_income_band_sk INNER JOIN filtered_customer_address_ad2 ad2 ON c_current_addr_sk = ad2.ca_address_sk INNER JOIN filtered_date_d2 d2 ON c_first_sales_date_sk = d2.d_date_sk INNER JOIN filtered_date_d3 d3 ON c_first_shipto_date_sk = d3.d_date_sk INNER JOIN filtered_promotion ON ss_promo_sk = p_promo_sk INNER JOIN item ON ss_item_sk = i_item_sk WHERE ss_wholesale_cost BETWEEN 35 AND 55 AND i_current_price BETWEEN 26 AND 36 AND cd1.cd_marital_status <> cd2.cd_marital_status GROUP BY i_product_name, i_item_sk, s_store_name, s_zip, ad1.ca_street_number, ad1.ca_street_name, ad1.ca_city, ad1.ca_zip, ad2.ca_street_number, ad2.ca_street_name, ad2.ca_city, ad2.ca_zip, d1.d_year, d2.d_year, d3.d_year), cross_sales AS (SELECT * FROM explicit_star_join) SELECT cs1.product_name, cs1.store_name, cs1.store_zip, cs1.b_street_number, cs1.b_street_name, cs1.b_city, cs1.b_zip, cs1.c_street_number, cs1.c_street_name, cs1.c_city, cs1.c_zip, cs1.syear, cs1.cnt, cs1.s1 AS s11, cs1.s2 AS s21, cs1.s3 AS s31, cs2.s1 AS s12, cs2.s2 AS s22, cs2.s3 AS s32, cs2.syear, cs2.cnt FROM cross_sales AS cs1, cross_sales AS cs2 WHERE cs1.item_sk = cs2.item_sk AND cs1.syear = 1998 AND cs2.syear = 1998 + 1 AND cs2.cnt <= cs1.cnt AND cs1.store_name = cs2.store_name AND cs1.store_zip = cs2.store_zip ORDER BY cs1.product_name, cs1.store_name, cs2.cnt, cs1.s1, cs2.s1