WITH filtered_store_sales AS (SELECT ss_sold_date_sk, ss_store_sk, ss_customer_sk, ss_cdemo_sk, ss_hdemo_sk, ss_addr_sk, ss_item_sk, ss_ticket_number, ss_promo_sk, ss_wholesale_cost, ss_list_price, ss_coupon_amt FROM store_sales WHERE ss_wholesale_cost BETWEEN 35 AND 55), filtered_cs_ui AS (select cs_item_sk, sum(cs_ext_list_price) as sale, sum(cr_refunded_cash+cr_reversed_charge+cr_store_credit) as refund from catalog_sales, catalog_returns where cs_item_sk = cr_item_sk and cs_order_number = cr_order_number and cs_wholesale_cost BETWEEN 35 AND 55 group by cs_item_sk having sum(cs_ext_list_price) > 2*sum(cr_refunded_cash+cr_reversed_charge+cr_store_credit)), cross_sales AS (SELECT i.i_product_name AS product_name, i.i_item_sk AS item_sk, s.s_store_name AS store_name, s.s_zip AS store_zip, ad1.ca_street_number AS b_street_number, ad1.ca_street_name AS b_street_name, ad1.ca_city AS b_city, ad1.ca_zip AS b_zip, ad2.ca_street_number AS c_street_number, ad2.ca_street_name AS c_street_name, ad2.ca_city AS c_city, ad2.ca_zip AS c_zip, d1.d_year AS syear, d2.d_year AS fsyear, d3.d_year AS s2year, COUNT(*) AS cnt, SUM(ss.ss_wholesale_cost) AS s1, SUM(ss.ss_list_price) AS s2, SUM(ss.ss_coupon_amt) AS s3 FROM filtered_store_sales ss INNER JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number INNER JOIN filtered_cs_ui ON ss.ss_item_sk = filtered_cs_ui.cs_item_sk INNER JOIN date_dim d1 ON ss.ss_sold_date_sk = d1.d_date_sk INNER JOIN store s ON ss.ss_store_sk = s.s_store_sk INNER JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk INNER JOIN customer_demographics cd1 ON ss.ss_cdemo_sk = cd1.cd_demo_sk INNER JOIN household_demographics hd1 ON ss.ss_hdemo_sk = hd1.hd_demo_sk INNER JOIN income_band ib1 ON hd1.hd_income_band_sk = ib1.ib_income_band_sk INNER JOIN customer_address ad1 ON ss.ss_addr_sk = ad1.ca_address_sk INNER JOIN customer_demographics cd2 ON c.c_current_cdemo_sk = cd2.cd_demo_sk INNER JOIN household_demographics hd2 ON c.c_current_hdemo_sk = hd2.hd_demo_sk INNER JOIN income_band ib2 ON hd2.hd_income_band_sk = ib2.ib_income_band_sk INNER JOIN customer_address ad2 ON c.c_current_addr_sk = ad2.ca_address_sk INNER JOIN date_dim d2 ON c.c_first_sales_date_sk = d2.d_date_sk INNER JOIN date_dim d3 ON c.c_first_shipto_date_sk = d3.d_date_sk INNER JOIN promotion p ON ss.ss_promo_sk = p.p_promo_sk INNER JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE i.i_current_price BETWEEN 26 AND 36 AND p.p_channel_email = 'N' AND p.p_channel_tv = 'Y' AND p.p_channel_radio = 'N' AND ad2.ca_state IN ('IA','IL','TX') AND cd1.cd_marital_status IN ('M','U') AND cd1.cd_education_status IN ('Unknown','Advanced Degree','College') AND cd2.cd_marital_status IN ('M','U') AND cd2.cd_education_status IN ('Unknown','Advanced Degree','College') AND cd1.cd_marital_status <> cd2.cd_marital_status GROUP BY i.i_product_name, i.i_item_sk, s.s_store_name, s.s_zip, ad1.ca_street_number, ad1.ca_street_name, ad1.ca_city, ad1.ca_zip, ad2.ca_street_number, ad2.ca_street_name, ad2.ca_city, ad2.ca_zip, d1.d_year, d2.d_year, d3.d_year) SELECT cs1.product_name, cs1.store_name, cs1.store_zip, cs1.b_street_number, cs1.b_street_name, cs1.b_city, cs1.b_zip, cs1.c_street_number, cs1.c_street_name, cs1.c_city, cs1.c_zip, cs1.syear, cs1.cnt, cs1.s1 AS s11, cs1.s2 AS s21, cs1.s3 AS s31, cs2.s1 AS s12, cs2.s2 AS s22, cs2.s3 AS s32, cs2.syear, cs2.cnt FROM cross_sales AS cs1, cross_sales AS cs2 WHERE cs1.item_sk = cs2.item_sk AND cs1.syear = 1998 AND cs2.syear = 1998 + 1 AND cs2.cnt <= cs1.cnt AND cs1.store_name = cs2.store_name AND cs1.store_zip = cs2.store_zip ORDER BY cs1.product_name, cs1.store_name, cs2.cnt, cs1.s1, cs2.s1