WITH filtered_sales AS (SELECT ss_store_sk, ss_item_sk, ss_sales_price FROM store_sales INNER JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_month_seq BETWEEN 1195 AND 1195 + 11 AND ss_sales_price / ss_list_price BETWEEN 0.38 AND 0.48), grouped_sales AS (SELECT ss_store_sk, ss_item_sk, SUM(ss_sales_price) AS revenue FROM filtered_sales GROUP BY ss_store_sk, ss_item_sk), store_avg AS (SELECT ss_store_sk, AVG(revenue) AS ave FROM grouped_sales GROUP BY ss_store_sk) SELECT s_store_name, i_item_desc, g.revenue, i_current_price, i_wholesale_cost, i_brand FROM grouped_sales g INNER JOIN store_avg a ON g.ss_store_sk = a.ss_store_sk INNER JOIN store ON store.s_store_sk = g.ss_store_sk AND store.s_state IN ('IA','IL','NC') INNER JOIN item ON item.i_item_sk = g.ss_item_sk AND item.i_manager_id BETWEEN 80 AND 84 WHERE g.revenue <= 0.1 * a.ave ORDER BY s_store_name, i_item_desc LIMIT 100