WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1215 AND 1226), filtered_store AS (SELECT s_store_sk, s_store_name FROM store WHERE s_state IN ('KS','OH','SD')), store_sales_joined AS (SELECT ss_store_sk, ss_item_sk, ss_sales_price FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk WHERE ss_sales_price / ss_list_price BETWEEN 0.79 AND 0.89), store_item_revenue AS (SELECT ss_store_sk, ss_item_sk, SUM(ss_sales_price) AS revenue FROM store_sales_joined GROUP BY ss_store_sk, ss_item_sk), store_avg AS (SELECT ss_store_sk, AVG(revenue) AS ave FROM store_item_revenue GROUP BY ss_store_sk) SELECT filtered_store.s_store_name, item.i_item_desc, store_item_revenue.revenue, item.i_current_price, item.i_wholesale_cost, item.i_brand FROM store_item_revenue INNER JOIN store_avg ON store_item_revenue.ss_store_sk = store_avg.ss_store_sk INNER JOIN filtered_store ON store_item_revenue.ss_store_sk = filtered_store.s_store_sk INNER JOIN item ON store_item_revenue.ss_item_sk = item.i_item_sk WHERE store_item_revenue.revenue <= 0.1 * store_avg.ave AND item.i_manager_id BETWEEN 10 AND 14 ORDER BY filtered_store.s_store_name, item.i_item_desc LIMIT 100