WITH store_sales_filtered AS (SELECT ss_store_sk, ss_item_sk, SUM(ss_sales_price) AS revenue FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_month_seq BETWEEN 1215 AND 1226 AND ss_sales_price / ss_list_price BETWEEN 79 * 0.01 AND 89 * 0.01 GROUP BY ss_store_sk, ss_item_sk), item_revenue_with_avg AS (SELECT ss_store_sk, ss_item_sk, revenue, AVG(revenue) OVER (PARTITION BY ss_store_sk) AS store_avg FROM store_sales_filtered), filtered_items AS (SELECT ss_store_sk, ss_item_sk, revenue FROM item_revenue_with_avg WHERE revenue <= 0.1 * store_avg), join_store_item AS (SELECT s_store_name, i_item_desc, fi.revenue, i_current_price, i_wholesale_cost, i_brand FROM filtered_items fi INNER JOIN store ON s_store_sk = fi.ss_store_sk INNER JOIN item ON i_item_sk = fi.ss_item_sk WHERE i_manager_id BETWEEN 10 AND 14 AND s_state IN ('KS','OH','SD')) SELECT s_store_name, i_item_desc, revenue, i_current_price, i_wholesale_cost, i_brand FROM join_store_item ORDER BY s_store_name, i_item_desc LIMIT 100