WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy BETWEEN 3 AND 5), store_customers AS (SELECT DISTINCT ss_customer_sk FROM store_sales JOIN filtered_date ON ss_sold_date_sk = filtered_date.d_date_sk WHERE ss_list_price BETWEEN 52 AND 141), web_customers AS (SELECT DISTINCT ws_bill_customer_sk FROM web_sales JOIN filtered_date ON ws_sold_date_sk = filtered_date.d_date_sk WHERE ws_list_price BETWEEN 52 AND 141), catalog_customers AS (SELECT DISTINCT cs_ship_customer_sk FROM catalog_sales JOIN filtered_date ON cs_sold_date_sk = filtered_date.d_date_sk WHERE cs_list_price BETWEEN 52 AND 141), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_state IN ('GA','KY','SD')), filtered_demo AS (SELECT cd_demo_sk, cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating FROM customer_demographics WHERE cd_marital_status IN ('D','W','U') AND cd_education_status IN ('Primary','College')), customer_base AS (SELECT c.c_customer_sk, filtered_demo.cd_gender, filtered_demo.cd_marital_status, filtered_demo.cd_education_status, filtered_demo.cd_purchase_estimate, filtered_demo.cd_credit_rating FROM customer c JOIN filtered_address ON c.c_current_addr_sk = filtered_address.ca_address_sk JOIN filtered_demo ON c.c_current_cdemo_sk = filtered_demo.cd_demo_sk), channel_joins AS (SELECT customer_base.*, store_customers.ss_customer_sk AS has_store, web_customers.ws_bill_customer_sk AS has_web, catalog_customers.cs_ship_customer_sk AS has_catalog FROM customer_base LEFT JOIN store_customers ON customer_base.c_customer_sk = store_customers.ss_customer_sk LEFT JOIN web_customers ON customer_base.c_customer_sk = web_customers.ws_bill_customer_sk LEFT JOIN catalog_customers ON customer_base.c_customer_sk = catalog_customers.cs_ship_customer_sk), filter AS (SELECT c_customer_sk, cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating FROM channel_joins WHERE has_store IS NOT NULL AND has_web IS NULL AND has_catalog IS NULL) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM filter GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating LIMIT 100