WITH filtered_d1 AS (SELECT d_date_sk, d_date, d_week_seq FROM date_dim WHERE d_year = 1998), filtered_d2 AS (SELECT d_date_sk, d_week_seq FROM date_dim), filtered_d3 AS (SELECT d_date_sk, d_date FROM date_dim), filtered_item AS (SELECT i_item_sk, i_item_desc FROM item WHERE i_category IN ('Children', 'Jewelry', 'Men')), filtered_cd AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_marital_status = 'U' AND cd_dep_count BETWEEN 9 AND 11), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential = '>10000'), pre_joined_fact AS (SELECT cs.cs_item_sk, cs.cs_quantity, cs.cs_promo_sk, cs.cs_order_number, d1.d_date AS d1_date, d1.d_week_seq AS d1_week_seq, i.i_item_desc FROM catalog_sales cs INNER JOIN filtered_d1 d1 ON cs.cs_sold_date_sk = d1.d_date_sk INNER JOIN filtered_item i ON cs.cs_item_sk = i.i_item_sk INNER JOIN filtered_cd cd ON cs.cs_bill_cdemo_sk = cd.cd_demo_sk INNER JOIN filtered_hd hd ON cs.cs_bill_hdemo_sk = hd.hd_demo_sk WHERE cs.cs_wholesale_cost BETWEEN 35 AND 55), inventory_join AS (SELECT pjf.i_item_desc, pjf.cs_item_sk, pjf.cs_promo_sk, pjf.cs_order_number, pjf.d1_week_seq, inv.inv_warehouse_sk FROM pre_joined_fact pjf INNER JOIN inventory inv ON pjf.cs_item_sk = inv.inv_item_sk INNER JOIN filtered_d2 d2 ON inv.inv_date_sk = d2.d_date_sk WHERE inv.inv_quantity_on_hand < pjf.cs_quantity AND pjf.d1_week_seq = d2.d_week_seq), final_join AS (SELECT ij.i_item_desc, w.w_warehouse_name, ij.d1_week_seq, ij.cs_item_sk, ij.cs_promo_sk, ij.cs_order_number, ij.inv_warehouse_sk FROM inventory_join ij INNER JOIN warehouse w ON ij.inv_warehouse_sk = w.w_warehouse_sk INNER JOIN filtered_d3 d3 ON d3.d_date > ij.d1_date + INTERVAL '3 day') SELECT i_item_desc, w_warehouse_name, d1_week_seq AS d_week_seq, SUM(CASE WHEN p.p_promo_sk IS NULL THEN 1 ELSE 0 END) AS no_promo, SUM(CASE WHEN p.p_promo_sk IS NOT NULL THEN 1 ELSE 0 END) AS promo, COUNT(*) AS total_cnt FROM final_join fj LEFT OUTER JOIN promotion p ON fj.cs_promo_sk = p.p_promo_sk LEFT OUTER JOIN catalog_returns cr ON cr.cr_item_sk = fj.cs_item_sk AND cr.cr_order_number = fj.cs_order_number GROUP BY i_item_desc, w_warehouse_name, d1_week_seq ORDER BY total_cnt DESC, i_item_desc, w_warehouse_name, d_week_seq LIMIT 100