WITH filtered_item AS (SELECT i_item_sk, i_brand_id, i_class_id, i_category_id, i_manufact_id FROM item WHERE i_category = 'Home'), filtered_date AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year IN (1998, 1999)), union_all_branches AS (SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, ss.ss_quantity - COALESCE(sr.sr_return_quantity,0) AS sales_cnt, ss.ss_ext_sales_price - COALESCE(sr.sr_return_amt,0.0) AS sales_amt FROM store_returns sr LEFT JOIN filtered_item i ON sr.sr_item_sk = i.i_item_sk JOIN store_sales ss ON sr.sr_ticket_number = ss.ss_ticket_number AND sr.sr_item_sk = ss.ss_item_sk JOIN filtered_date d ON ss.ss_sold_date_sk = d.d_date_sk WHERE sr.sr_reason_sk IN (7,25,26,52,69) AND (ss.ss_sales_price / ss.ss_list_price) BETWEEN 0.34 AND 0.54 UNION ALL SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, cs.cs_quantity - COALESCE(cr.cr_return_quantity,0) AS sales_cnt, cs.cs_ext_sales_price - COALESCE(cr.cr_return_amount,0.0) AS sales_amt FROM catalog_returns cr LEFT JOIN filtered_item i ON cr.cr_item_sk = i.i_item_sk JOIN catalog_sales cs ON cr.cr_order_number = cs.cs_order_number AND cr.cr_item_sk = cs.cs_item_sk JOIN filtered_date d ON cs.cs_sold_date_sk = d.d_date_sk WHERE cr.cr_reason_sk IN (7,25,26,52,69) AND (cs.cs_sales_price / cs.cs_list_price) BETWEEN 0.34 AND 0.54 UNION ALL SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, ws.ws_quantity - COALESCE(wr.wr_return_quantity,0) AS sales_cnt, ws.ws_ext_sales_price - COALESCE(wr.wr_return_amt,0.0) AS sales_amt FROM web_returns wr LEFT JOIN filtered_item i ON wr.wr_item_sk = i.i_item_sk JOIN web_sales ws ON wr.wr_order_number = ws.ws_order_number AND wr.wr_item_sk = ws.ws_item_sk JOIN filtered_date d ON ws.ws_sold_date_sk = d.d_date_sk WHERE wr.wr_reason_sk IN (7,25,26,52,69) AND (ws.ws_sales_price / ws.ws_list_price) BETWEEN 0.34 AND 0.54), channel_aggregate AS (SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, SUM(sales_cnt) AS sales_cnt, SUM(sales_amt) AS sales_amt FROM union_all_branches GROUP BY d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id) SELECT prev_yr.d_year AS prev_year, curr_yr.d_year AS year, curr_yr.i_brand_id, curr_yr.i_class_id, curr_yr.i_category_id, curr_yr.i_manufact_id, prev_yr.sales_cnt AS prev_yr_cnt, curr_yr.sales_cnt AS curr_yr_cnt, curr_yr.sales_cnt-prev_yr.sales_cnt AS sales_cnt_diff, curr_yr.sales_amt-prev_yr.sales_amt AS sales_amt_diff FROM channel_aggregate curr_yr, channel_aggregate prev_yr WHERE curr_yr.i_brand_id=prev_yr.i_brand_id AND curr_yr.i_class_id=prev_yr.i_class_id AND curr_yr.i_category_id=prev_yr.i_category_id AND curr_yr.i_manufact_id=prev_yr.i_manufact_id AND curr_yr.d_year=1999 AND prev_yr.d_year=1999-1 AND prev_yr.sales_cnt > 0 AND CAST(curr_yr.sales_cnt AS DECIMAL(17,2))/CAST(prev_yr.sales_cnt AS DECIMAL(17,2))<0.9 ORDER BY sales_cnt_diff, sales_amt_diff LIMIT 100