WITH filtered_item AS (SELECT i_item_sk, i_brand_id, i_class_id, i_category_id, i_manufact_id FROM item WHERE i_category = 'Children'), filtered_date AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year IN (1998, 1999)), channel_aggregates AS (SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, SUM(catalog_cnt) + SUM(store_cnt) + SUM(web_cnt) AS sales_cnt, SUM(catalog_amt) + SUM(store_amt) + SUM(web_amt) AS sales_amt FROM (SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, SUM(CASE WHEN channel = 'catalog' THEN sales_cnt ELSE 0 END) AS catalog_cnt, SUM(CASE WHEN channel = 'catalog' THEN sales_amt ELSE 0 END) AS catalog_amt, SUM(CASE WHEN channel = 'store' THEN sales_cnt ELSE 0 END) AS store_cnt, SUM(CASE WHEN channel = 'store' THEN sales_amt ELSE 0 END) AS store_amt, SUM(CASE WHEN channel = 'web' THEN sales_cnt ELSE 0 END) AS web_cnt, SUM(CASE WHEN channel = 'web' THEN sales_amt ELSE 0 END) AS web_amt FROM (SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, 'catalog' AS channel, cs_quantity - COALESCE(cr_return_quantity, 0) AS sales_cnt, cs_ext_sales_price - COALESCE(cr_return_amount, 0.0) AS sales_amt FROM catalog_sales JOIN filtered_item ON i_item_sk = cs_item_sk JOIN filtered_date ON d_date_sk = cs_sold_date_sk LEFT JOIN catalog_returns ON (cs_order_number = cr_order_number AND cs_item_sk = cr_item_sk) WHERE cs_sales_price / cs_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND cr_reason_sk IN (8, 18, 20, 23, 41) UNION ALL SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, 'store' AS channel, ss_quantity - COALESCE(sr_return_quantity, 0) AS sales_cnt, ss_ext_sales_price - COALESCE(sr_return_amt, 0.0) AS sales_amt FROM store_sales JOIN filtered_item ON i_item_sk = ss_item_sk JOIN filtered_date ON d_date_sk = ss_sold_date_sk LEFT JOIN store_returns ON (ss_ticket_number = sr_ticket_number AND ss_item_sk = sr_item_sk) WHERE ss_sales_price / ss_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND sr_reason_sk IN (8, 18, 20, 23, 41) UNION ALL SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, 'web' AS channel, ws_quantity - COALESCE(wr_return_quantity, 0) AS sales_cnt, ws_ext_sales_price - COALESCE(wr_return_amt, 0.0) AS sales_amt FROM web_sales JOIN filtered_item ON i_item_sk = ws_item_sk JOIN filtered_date ON d_date_sk = ws_sold_date_sk LEFT JOIN web_returns ON (ws_order_number = wr_order_number AND ws_item_sk = wr_item_sk) WHERE ws_sales_price / ws_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND wr_reason_sk IN (8, 18, 20, 23, 41)) channel_detail GROUP BY d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id) channel_pivot GROUP BY d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id) SELECT prev_yr.d_year AS prev_year, curr_yr.d_year AS year, curr_yr.i_brand_id, curr_yr.i_class_id, curr_yr.i_category_id, curr_yr.i_manufact_id, prev_yr.sales_cnt AS prev_yr_cnt, curr_yr.sales_cnt AS curr_yr_cnt, curr_yr.sales_cnt - prev_yr.sales_cnt AS sales_cnt_diff, curr_yr.sales_amt - prev_yr.sales_amt AS sales_amt_diff FROM channel_aggregates curr_yr, channel_aggregates prev_yr WHERE curr_yr.i_brand_id = prev_yr.i_brand_id AND curr_yr.i_class_id = prev_yr.i_class_id AND curr_yr.i_category_id = prev_yr.i_category_id AND curr_yr.i_manufact_id = prev_yr.i_manufact_id AND curr_yr.d_year = 1999 AND prev_yr.d_year = 1998 AND prev_yr.sales_cnt > 0 AND CAST(curr_yr.sales_cnt AS DECIMAL(17,2)) / CAST(prev_yr.sales_cnt AS DECIMAL(17,2)) < 0.9 ORDER BY sales_cnt_diff, sales_amt_diff LIMIT 100