WITH date_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('1999-10-21' AS DATE) AND CAST('1999-10-21' AS DATE) + INTERVAL '30 DAY'), item_filtered AS (SELECT i_item_sk, i_current_price, i_category FROM item WHERE i_current_price > 50 AND i_category IN ('Men', 'Music')), promo_filtered AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'Y' AND p_channel_tv = 'N' AND p_channel_radio = 'N' AND p_channel_press = 'N' AND p_channel_event = 'N'), ssr AS (SELECT s_store_id AS store_id, SUM(ss_ext_sales_price) AS sales, SUM(COALESCE(sr_return_amt, 0)) AS returns, SUM(ss_net_profit - COALESCE(sr_net_loss, 0)) AS profit FROM store_sales LEFT OUTER JOIN store_returns ON (ss_item_sk = sr_item_sk AND ss_ticket_number = sr_ticket_number) INNER JOIN date_filtered ON ss_sold_date_sk = date_filtered.d_date_sk INNER JOIN store ON ss_store_sk = s_store_sk INNER JOIN item_filtered ON ss_item_sk = item_filtered.i_item_sk INNER JOIN promo_filtered ON ss_promo_sk = promo_filtered.p_promo_sk WHERE ss_wholesale_cost BETWEEN 21 AND 36 GROUP BY s_store_id), csr AS (SELECT cp_catalog_page_id AS catalog_page_id, SUM(cs_ext_sales_price) AS sales, SUM(COALESCE(cr_return_amount, 0)) AS returns, SUM(cs_net_profit - COALESCE(cr_net_loss, 0)) AS profit FROM catalog_sales LEFT OUTER JOIN catalog_returns ON (cs_item_sk = cr_item_sk AND cs_order_number = cr_order_number) INNER JOIN date_filtered ON cs_sold_date_sk = date_filtered.d_date_sk INNER JOIN catalog_page ON cs_catalog_page_sk = cp_catalog_page_sk INNER JOIN item_filtered ON cs_item_sk = item_filtered.i_item_sk INNER JOIN promo_filtered ON cs_promo_sk = promo_filtered.p_promo_sk WHERE cs_wholesale_cost BETWEEN 21 AND 36 GROUP BY cp_catalog_page_id), wsr AS (SELECT web_site_id, SUM(ws_ext_sales_price) AS sales, SUM(COALESCE(wr_return_amt, 0)) AS returns, SUM(ws_net_profit - COALESCE(wr_net_loss, 0)) AS profit FROM web_sales LEFT OUTER JOIN web_returns ON (ws_item_sk = wr_item_sk AND ws_order_number = wr_order_number) INNER JOIN date_filtered ON ws_sold_date_sk = date_filtered.d_date_sk INNER JOIN web_site ON ws_web_site_sk = web_site_sk INNER JOIN item_filtered ON ws_item_sk = item_filtered.i_item_sk INNER JOIN promo_filtered ON ws_promo_sk = promo_filtered.p_promo_sk WHERE ws_wholesale_cost BETWEEN 21 AND 36 GROUP BY web_site_id) SELECT channel, id, SUM(sales) AS sales, SUM(returns) AS returns, SUM(profit) AS profit FROM (SELECT 'store channel' AS channel, 'store' || store_id AS id, sales, returns, profit FROM ssr UNION ALL SELECT 'catalog channel' AS channel, 'catalog_page' || catalog_page_id AS id, sales, returns, profit FROM csr UNION ALL SELECT 'web channel' AS channel, 'web_site' || web_site_id AS id, sales, returns, profit FROM wsr) AS x GROUP BY ROLLUP (channel, id) ORDER BY channel, id LIMIT 100