WITH returns_cte AS (SELECT cr_returning_customer_sk AS ctr_customer_sk, returning_addr.ca_state AS ctr_state, SUM(cr_return_amt_inc_tax) AS ctr_total_return FROM catalog_returns JOIN date_dim ON cr_returned_date_sk = d_date_sk JOIN customer_address AS returning_addr ON cr_returning_addr_sk = returning_addr.ca_address_sk WHERE d_year = 2000 GROUP BY cr_returning_customer_sk, returning_addr.ca_state), state_avg_cte AS (SELECT ctr_state, AVG(ctr_total_return) AS state_avg FROM returns_cte GROUP BY ctr_state), filtered_customers AS (SELECT c_customer_sk, ctr1.ctr_total_return FROM returns_cte ctr1 JOIN state_avg_cte ON ctr1.ctr_state = state_avg_cte.ctr_state AND ctr1.ctr_total_return > state_avg_cte.state_avg * 1.2 JOIN customer ON ctr1.ctr_customer_sk = c_customer_sk WHERE EXISTS (SELECT 1 FROM customer_address WHERE ca_address_sk = c_current_addr_sk AND ca_state = 'IA')) SELECT c_customer_id, c_salutation, c_first_name, c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country, ca.ca_gmt_offset, ca.ca_location_type, fc.ctr_total_return FROM filtered_customers fc JOIN LATERAL (SELECT * FROM customer_address WHERE ca_address_sk = c_current_addr_sk) ca ON true ORDER BY c_customer_id, c_salutation, c_first_name, c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country, ca.ca_gmt_offset, ca.ca_location_type, fc.ctr_total_return LIMIT 100